import boto3

def update_publish(tableName, item):
  client = boto3.client('dynamodb')
  response = client.update_item(
    TableName=tableName,
    Key={
      'bot': {
        'S': item['bot']['S']
      },
      'tweet_date': {
        'S': item['tweet_date']['S']
      }
    },
    AttributeUpdates={
      'published': {
        'Value': {
          'BOOL': True
        },
        'Action': 'PUT'
      }
    }
  )
  return response

def get_items(tableName):
  client = boto3.client('dynamodb')
  response = client.scan(
    TableName = tableName
  )
  return response